import {createReducer} from '@reduxjs/toolkit';
import { cityChanger, offersChangerByCity, activeSortOption } from './action';
import offers from '../mocks/offers';

const cities: string[] = ['Paris', 'Cologne', 'Brussels', 'Amsterdam', 'Hamburg', 'Dusseldorf'];
const sortOptions: string[] = ['Popular', 'Price: low to high', 'Price: high to low', 'Top rated first'];

const initialState = {
  cities: cities,
  city: cities[0],
  offers: offers,
  activeSortOption: 'Popular',
  sortOptions: sortOptions,
};

const reducer = createReducer(initialState, (builder) => {
  builder
    .addCase(cityChanger, (state, action) => {
      state.city = action.payload.city;
    });
  builder
    .addCase(offersChangerByCity, (state) => {
      state.offers = initialState.offers.filter((offer) => offer.city.name === state.city);
    });
  builder
    .addCase(activeSortOption, (state, action) => {
      state.activeSortOption = action.payload.option;
      switch(action.payload.option) {
        case 'Popular':
          state.offers = state.offers = initialState.offers.filter((offer) => offer.city.name === state.city);
          break;
        case 'Price: low to high':
          state.offers.sort((a,b) => b.price - a.price);
          break;
        case 'Price: high to low':
          state.offers.sort((a,b) => a.price - b.price);
          break;
        case 'Top rated first':
          state.offers.sort((a,b) => a.rating - b.rating);
          break;
      }
    });


});

export {reducer};
