import {createReducer} from '@reduxjs/toolkit';
import { cityChanger } from './action';

const initialState = {
  city: '',
  offers: [],
};

const reducer = createReducer(initialState, (builder) => {
  builder
    .addCase(cityChanger, (state) => {
      state.city = 'KKK';
    });

});

export {reducer};
