import { Offers } from './types/offers';

export const sortOffers = (offers: Offers, sortOption: string, activeCity: string): Offers => {
  switch(sortOption) {
    case 'Popular':
      offers.filter((offer) => offer.city.name === activeCity);
      break;
    case 'Price: low to high':
      offers.sort((a,b) => a.price - b.price).filter((offer) => offer.city.name === activeCity);
      break;
    case 'Price: high to low':
      offers.sort((a,b) => b.price - a.price).filter((offer) => offer.city.name === activeCity);
      break;
    case 'Top rated first':
      offers.sort((a,b) => b.rating - a.rating).filter((offer) => offer.city.name === activeCity);
      break;
  }
  console.log(offers, sortOption, activeCity);
  return offers;
};
