import {useState, ChangeEvent, FormEvent} from 'react';
import { useAppDispatch } from '../../hooks';
import { sendCommentAction } from '../../store/api-actions';
import { UserComment } from '../../types/comments';

function CommentSubmitForm(): JSX.Element {
  const [review, setReview] = useState({comment: '', rating: 0});

  const dispatch = useAppDispatch();

  const onSubmit = ({comment, rating}: UserComment) => {
    dispatch(sendCommentAction({
      comment,
      rating,
      date: '',
      id: 0,
      user: {
        avatarUrl: '',
        id: 0,
        isPro: false,
        name: ''
      }
    }));
  };

  const handleSubmit = (evt) => {
    evt.preventDefault();

    if (review.comment !== null && review.rating !== null) {
      onSubmit({
        comment: review.comment,
        rating: review.rating,
        date: '',
        id: 0,
        user: {
          avatarUrl: '',
          id: 0,
          isPro: false,
          name: ''
        }
      });
    }
  };

  return (
    <form
      className="reviews__form form"
      action="#"
      method="post"
      //onSubmit={handleSubmit}
    >
      <label className="reviews__label form__label" htmlFor="review">Your review</label>
      <div
        onChange={(evt: ChangeEvent<HTMLInputElement>) =>
          setReview({...review, rating: Number(evt.target.value)})}
        className="reviews__rating-form form__rating"
      >
        <input className="form__rating-input visually-hidden" name="rating" value="5" id="5-stars" type="radio" />
        <label htmlFor="5-stars" className="reviews__rating-label form__rating-label" title="perfect">
          <svg className="form__star-image" width="37" height="33">
            <use xlinkHref="#icon-star"></use>
          </svg>
        </label>

        <input className="form__rating-input visually-hidden" name="rating" value="4" id="4-stars" type="radio" />
        <label htmlFor="4-stars" className="reviews__rating-label form__rating-label" title="good">
          <svg className="form__star-image" width="37" height="33">
            <use xlinkHref="#icon-star"></use>
          </svg>
        </label>

        <input className="form__rating-input visually-hidden" name="rating" value="3" id="3-stars" type="radio" />
        <label htmlFor="3-stars" className="reviews__rating-label form__rating-label" title="not bad">
          <svg className="form__star-image" width="37" height="33">
            <use xlinkHref="#icon-star"></use>
          </svg>
        </label>

        <input className="form__rating-input visually-hidden" name="rating" value="2" id="2-stars" type="radio" />
        <label htmlFor="2-stars" className="reviews__rating-label form__rating-label" title="badly">
          <svg className="form__star-image" width="37" height="33">
            <use xlinkHref="#icon-star"></use>
          </svg>
        </label>

        <input className="form__rating-input visually-hidden" name="rating" value="1" id="1-star" type="radio" />
        <label htmlFor="1-star" className="reviews__rating-label form__rating-label" title="terribly">
          <svg className="form__star-image" width="37" height="33">
            <use xlinkHref="#icon-star"></use>
          </svg>
        </label>
      </div>
      <textarea
        onChange={(evt: ChangeEvent<HTMLTextAreaElement>) =>
          setReview({...review, comment: evt.target.value})}
        className="reviews__textarea form__textarea" id="review" name="review" placeholder="Tell how was your stay, what you like and what can be improved"
      >
      </textarea>
      <div className="reviews__button-wrapper">
        <p className="reviews__help">
                                To submit review please make sure to set <span className="reviews__star">rating</span> and describe your stay with at least <b className="reviews__text-amount">50 characters</b>.
        </p>
        <button onClick={handleSubmit} className="reviews__submit form__submit button" type="submit">Submit</button>
      </div>
    </form>

  );
}

export default CommentSubmitForm;
