import { Offers } from './types/offers';

export const sortOffers = (offers: Offers, sortOption: string, activeCity: string): Offers => {
  let sortedOffers: Offers = [...offers];
  switch(sortOption) {
    case 'Popular':
      sortedOffers = sortedOffers.filter((offer) => offer.city.name === activeCity);
      break;
    case 'Price: low to high':
      sortedOffers = sortedOffers.sort((a,b) => a.price - b.price).filter((offer) => offer.city.name === activeCity);
      break;
    case 'Price: high to low':
      sortedOffers = sortedOffers.sort((a,b) => b.price - a.price).filter((offer) => offer.city.name === activeCity);
      break;
    case 'Top rated first':
      sortedOffers = sortedOffers.sort((a,b) => b.rating - a.rating).filter((offer) => offer.city.name === activeCity);
      break;
  }
  return sortedOffers;
};
